function login_form(){const o=document.querySelector(".login-form"),e=o.email.value,a=o.senha.value;$.ajax({type:"post",url:"/canal_start/admin/validation/login.php",data:{email:e,senha:a},success:function(o){"login efetuado"==o&&(window.location.href="painel/dashboard"),"login não encontrado"==o&&alert("Seu login não existe em nosso sistema! Caso tenha esquecido seu login entre em contato com o administrador o mais rápido possível.")}})}$(".login-help").click(function(){alert("Está perdido? Para obter ajuda de qualquer tipo entre em contato com o administrador ou desenvolvedor o mas rápido possível!")});