function login_form(){const e=document.querySelector(".login-form"),o=e.email.value,a=e.senha.value;$.ajax({type:"post",url:"/canal_start/admin/validation/login.php",data:{email:o,senha:a},success:function(e){switch(e){case"sucesso":window.location.href="painel/inicio";break;case"error":alert("Seu login não foi encontrado! Verifique os dados e tente novamente.");break;default:alert("Ocorreu um erro ao validar o login! Procure o desenvolvedor e verifique o console"),console.log(e)}}})}function profile_edit(){const e=document.querySelector(".profileEdit-form"),o=e.id.value,a=e.email.value,l=e.senha.value,n=e.nome.value,r=e.apelido.value,i=e.avatar.files[0];if(null==i)$.ajax({type:"post",url:"/canal_start/admin/validation/profile.php?avatar=no",data:{id:o,email:a,senha:l,nome:n,apelido:r},success:function(e){"sucesso"==e?(alert("Perfil editado com sucesso"),location.reload()):(alert("Ocorreu um erro, procure o desenvolvedor!"),console.log(e))}});else{const e=new FileReader;e.readAsDataURL(i),e.onload=function(){const i=e.result;$.ajax({type:"post",url:"/canal_start/admin/validation/profile.php?avatar=yes",data:{id:o,email:a,senha:l,nome:n,apelido:r,avatar:i},success:function(e){"sucesso"==e?(alert("Perfil atualizado com sucesso"),location.reload()):(alert("Ocorreu um erro, procure o desenvolvedor!"),console.log(e))}})}}}$(".login-help").click(function(){alert("Está perdido? Para obter ajuda de qualquer tipo entre em contato com o administrador ou desenvolvedor o mas rápido possível!")}),$(".sidenav-toggle, .sidenav-close").click(function(){$(".sidenav").animate({width:"toggle"},500)});